import re
import random

# roll result enum
class Fail():
    def __repr__(self):
        return "FAIL"
FAIL = Fail()

class Partial():
    def __repr__(self):
        return "PARTIAL"
PARTIAL = Partial()

class Success():
    def __repr__(self):
        return "SUCCESS"
SUCCESS = Success()

class Critical():
    def __repr__(self):
        return "CRITICAL"
CRITICAL = Critical()


def roll(n):
    """Roll nD6 and return a list of rolls"""
    return [random.randint(1, 6) for _ in range(n)]

def determine_result(rolls):
    """Determine the result based on the rolls"""
    if rolls.count(6) >= 3:
        return CRITICAL
    if 6 in rolls:
        return SUCCESS
    if rolls.count(5) >= 3:
        return SUCCESS
    if 5  in rolls:
        return PARTIAL
    if 4 in rolls:
